
public class Driver {
	private int age;
	private String name;
	
	public Driver(int age, String name) {
		this.age = age;
		this.name = name;
	}

	public String getName() {
		// TODO Auto-generated method stub
		return name;
	}

	public int getAge() {
		// TODO Auto-generated method stub
		return age;
	}

}
